mapper.delete:
DELETE FROM
tb_grade_subject
WHERE
subject_id=#{subjectId}
AND user_id=#{userId}

mapper.update:
update tb_grade_subject
subject_id=#{subjectId}, subject_name=#{subjectName}, subject_desc=#{subjectDesc}, subject_score=#{subjectScore}, excel_id=#{excelId}
where subject_id=#{subjectId}
