mapper.delete:
DELETE FROM
tb_reach_point
WHERE
reach_point_id=#{reachPointId}
AND user_id=#{userId}

mapper.update:
update tb_reach_point
excel_id=#{excelId}, point_id_one=#{pointIdOne}, point_id_two=#{pointIdTwo}, reach_score_one=#{reachScoreOne}, reach_score_two=#{reachScoreTwo}, finall_reach_score_one=#{finallReachScoreOne}, finall_reach_score_two=#{finallReachScoreTwo}, point_desc_one=#{pointDescOne}, point_desc_two=#{pointDescTwo}, teach_target_one=#{teachTargetOne}, teach_target_two=#{teachTargetTwo}, reach_way_one=#{reachWayOne}, reach_way_two=#{reachWayTwo}, evaluation_basis_one=#{evaluationBasisOne}, evaluation_basis_two=#{evaluationBasisTwo}, work_grade=#{workGrade}, class_grade=#{classGrade}, exp_grade=#{expGrade}, last_grade=#{lastGrade}, user_id=#{userId}, point_graduation=#{pointGraduation}, all_grade_one=#{allGradeOne}, all_grade_two=#{allGradeTwo}, reach_record=#{reachRecord}, reach_change=#{reachChange}, reach_resources=#{reachResources}
where reach_point_id=#{reachPointId}
